
;--------------------------------------;
; Duck Tales - Music Box v.1.1 (60 Гц) ;
;--------------------------------------;
        .PHASE 100h
h_total      equ  8 + 64 + 6
h_sync       equ  18;8

v_total      equ  3 + 20 + 2
v_sync       equ  2;1

screen_size  equ  h_total*v_total
screen_addr  equ  0bff3h-screen_size

row_lines    equ  16;10
under_line   equ  16;10
spaced_row   equ  0
line_offset  equ  1
spaced_atr   equ  1
cursor_type  equ  1
include RK86.MAC
crt_prm_port  equ  DISP
crt_cmd_port  equ  crt_prm_port+1
crt_init1     equ  spaced_row*128 + h_total-1
crt_init2     equ  (v_sync-1)*64 + v_total-1
crt_init3     equ  (under_line-1)*16 + row_lines-1
crt_init4     equ  line_offset*128 + spaced_atr*64 + cursor_type*16 + h_sync/2-1
crt_reset     equ  0
crt_start     equ  027h
crt_cursor    equ  080h
crt_preset    equ  0e0h
crt_ir_flag   equ  020h

dma_mode_port  equ  DMA 
dma_init1      equ  Low(screen_addr)
dma_init2      equ  High(screen_addr)
dma_init3      equ  Low(4000h + screen_size-1)
dma_init4      equ  High(4000h + screen_size-1)
dma_reset      equ  080h
dma_start      equ  0a4h

pit_ch1_port   equ  PSG1
pit_ch2_port   equ  pit_ch1_port+1
pit_ch3_port   equ  pit_ch1_port+2
pit_mode_port  equ  pit_ch1_port+3
pit_ch1_stop   equ  03eh
pit_ch2_stop   equ  07eh
pit_ch3_stop   equ  0beh

key_line_port  equ  PPI
key_data_port  equ  key_line_port+1
key_tape_port  equ  key_line_port+2
key_line2      equ  0fbh
key_0          equ  11111110b
key_1          equ  11111101b
key_2          equ  11111011b
key_3          equ  11110111b
key_4          equ  11101111b
key_5          equ  11011111b
key_6          equ  10111111b
key_7          equ  01111111b
no_key         equ  11111111b
shift          equ  11011111b

print_text     equ  0f818h
display_reset  equ  0f82dh
command_line   equ  0f875h

;-------------------------------
; program_begin:

; --- титульный экран ---

  lxi  h, text1
  call print_text
  pchl

text1:
db 31,10,13,'MUSIC THEME FROM DUCK TALES',10,13,10,13
db '[1] - "TITLE SCREEN"',10,13
db '[2] - "THE AMAZON"',10,13
db '[3] - "TRANSYLVANIA"',10,13
db '[4] - "AFRICAN MINES"',10,13
db '[5] - "THE HIMALAYAS"',10,13
db '[6] - "THE MOON"',10,13
db '[7] - "BONUS STAGE"',10,13,10,13
db '[0] - STOP SOUND',10,13,10,13,10,13
db '[ss]+[0] EXIT TO MONITOR',10,13,10,13,10,13
db '(C) 2018 - SEGABOY - ZX-PK.RU',10,13,0

; --- настройка дисплея ---
IF 0
  lxi  h, crt_cmd_port
  mvi  m, crt_reset
  dcr  l
  mvi  m, crt_init1
  mvi  m, crt_init2
  mvi  m, crt_init3
  mvi  m, crt_init4
  inr  l
  mvi  m, crt_cursor
  dcr  l
  mvi  m, 0ffh
  mvi  m, 0ffh
  inr  l
  mvi  m, crt_start
  mov  a, m
  mov  a, m
  ani  crt_ir_flag
  jz   $-3
  lxi  h, dma_mode_port
  mvi  m, dma_reset
  mvi  l, 4
  mvi  m, dma_init1
  mvi  m, dma_init2
  inr  l
  mvi  m, dma_init3
  mvi  m, dma_init4
  mvi  l, 8
  mvi  m, dma_start
ENDIF
; --- начальные установки ---

  xra  a
  sta  play_time
  mvi  a, key_line2
  sta  key_line_port

; ----- ожидаем конец кадра

wait_vsync:
  lda  crt_cmd_port
  lda  crt_cmd_port
  ani  crt_ir_flag
  jz   wait_vsync+3

; --- опрос клавиатуры ---

  lda  key_data_port   ; получаем значение из порта и проверяем нажатие
  cpi  no_key          ; если клавиша нажата, то
  jnz  stop_music      ; переходим к выключению музыки

  lda  play_time       ; проверяем переменную с количеством повторений
  ora  a               ; если переменная равна нулю, то
  jz   wait_vsync      ; остаёмся в цикле ожидания конца кадра

  lxi  h, wait_counter ; проверяем счётчик кадров
  dcr  m               ; пока он не равен нулю
  jnz  wait_vsync      ; остаёмся в цикле ожидания конца кадра
  
; --- читаем данные и управляем каналами ---

read_data:
  lhld data_adr        ; указатель на текущий адрес данных
  mov  a, m            ; читаем байт
  inx  h               ; увеличиваем
  shld data_adr        ; и сохраняем указатель

  ora  a               ; проверяем на ноль
  jz   end_of_music    ; если ноль, то это конец музыки
                       ; иначе это таймер
  mov  c, a            ; сохраняем значение
  ani  00011111b       ; убираем флаги
  jz   off_channel     ; если ноль, то переходим к выключению каналов
  sta  wait_counter    ; иначе устанавливаем таймер

  mov  a, c            ; восстанавливаем значение флагов
  lxi  h, pit_ch1_port ; выбираем порт канала 1
  rlc                  ; выдвигаем бит
  mov  c, a            ; сохраняем значение
  cc   set_channel     ; если бит установлен, то это канал 1

  mov  a, c            ; восстанавливаем значение флагов
  lxi  h, pit_ch2_port ; выбираем порт канала 2
  rlc                  ; выдвигаем бит
  mov  c, a            ; сохраняем значение
  cc   set_channel     ; если бит установлен, то это канал 2

  mov  a, c            ; восстанавливаем значение флагов
  lxi  h, pit_ch3_port ; выбираем порт канала 3
  rlc                  ; выдвигаем бит
  cc   set_channel     ; если бит установлен, то это канал 3

  jmp  wait_vsync

; --- выключение каналов ---

off_channel:
  lxi  h, pit_mode_port ; выбираем порт режимов
  mov  a, c             ; восстанавливаем значение флагов
  rlc                   ; выдвигаем флаг канала 1
  jnc  $+5              ; если сброшен, пропускаем следующий шаг
  mvi  m, pit_ch1_stop  ; выключаем канал 1
  rlc                   ; выдвигаем флаг канала 2
  jnc  $+5              ; если сброшен, пропускаем следующий шаг
  mvi  m, pit_ch2_stop  ; выключаем канал 2
  rlc                   ; выдвигаем бит канала 3
  jnc  $+5              ; если сброшен, пропускаем следующий шаг
  mvi  m, pit_ch3_stop  ; выключаем канал 3
  jmp  read_data

; --- установка каналов ---

set_channel:
  push h               ; сохраняем номер порта в стек
  lhld data_adr        ; указатель на текущий адрес данных
  mov  a, m            ; загружаем байт
  inx  h               ; увеличиваем
  shld data_adr        ; и сохраняем указатель

  lhld table_adr      ; в регистре hl указатель на таблицу нот
  mvi  d, 0
  add  a              ; умножаем номер ноты на 2, получаем смещение в таблице
  jnc  $+4            ; если нет переполнения, то пропускаем следующий шаг
  inr  d              ;
  mov  e, a           ; помещаем смещение в регистр de
  dad  d              ; получаем в регистре hl адрес константы для ноты
  pop  d              ; восстанавливаем номер порта
  mov  a, m           ; загружаем младший байт в аккумулятор
  stax d              ; и посылаем его в порт
  inx  h              ; переходим к старшему байту
  mov  a, m           ; загружаем старший байт в аккумулятор
  stax d              ; и посылаем его в порт
  ret

; --- конец мелодии ---

end_of_music:
  lxi  h, play_time  ; уменьшаем переменную с количеством повторов
  dcr  m             ; если она равна нулю, то
  jz   stop_music    ; переходим к выключению музыки

  lhld repeat_adr    ; иначе загружаем значение из адреса повтора
  shld data_adr      ; и помещаем его указатель на адрес данных
  jmp  read_data

; --- выключаем музыку ---

stop_music:
  lxi  h, pit_mode_port
  mvi  m, pit_ch1_stop
  mvi  m, pit_ch2_stop
  mvi  m, pit_ch3_stop

  lxi  h, play_time   ;
  mvi  m, 0           ; обнуляем счётчик повторов
  
; --- проверяем нажатые клавиши ---

read_1:
  cpi  key_1
  jnz  read_2
  lxi  h, table_1
  lxi  d, data_1
  lxi  b, repeat_1
  mvi  a, 2
  jmp  music_selected

read_2:
  cpi  key_2
  jnz  read_3
  lxi  h, table_2
  lxi  d, data_2
  lxi  b, repeat_2
  mvi  a, 2
  jmp  music_selected

read_3:
  cpi  key_3
  jnz  read_4
  lxi  h, table_3
  lxi  d, data_3
  lxi  b, repeat_3
  mvi  a, 2
  jmp  music_selected

read_4:
  cpi  key_4
  jnz  read_5
  lxi  h, table_4
  lxi  d, data_4
  lxi  b, repeat_4
  mvi  a, 2
  jmp  music_selected

read_5:
  cpi  key_5
  jnz  read_6
  lxi  h, table_5
  lxi  d, data_5
  lxi  b, repeat_5
  mvi  a, 2
  jmp  music_selected

read_6:
  cpi  key_6
  jnz  read_7
  lxi  h, table_6
  lxi  d, data_6
  lxi  b, repeat_6
  mvi  a, 2
  jmp  music_selected

read_7:
  cpi  key_7
  jnz  read_0
  lxi  h, table_7
  lxi  d, data_7
  lxi  b, repeat_7
  mvi  a, 4

; --- мелодия выбрана ---

music_selected:
  sta  play_time    ; сохраняем счётчик повторов
  shld table_adr    ; сохраняем указатель на таблицу нот
  xchg              ;
  shld data_adr     ; сохраняем указатель на начало данных
  mov  h, b         ;
  mov  l, c         ;
  shld repeat_adr   ; сохраняем указатель на точку повтора
  mvi  a, 20        ;
  sta  wait_counter ; задержка
  jmp  wait_vsync

read_0:
  lda  key_tape_port
  rlc
  rlc
  rlc
  jc  wait_vsync

; --- конец программы ---

end_program:
  call display_reset
  jmp  command_line  


